def test_mIWrap():
	msg = 'Please enter a media file to pull information from: '
	selection = miOps.getInput(msg)
	selection = selection.rstrip()
	requests = ['General;FileExtension', 'General;FileSize', 'General;Duration', 'General;OverallBitRate']
	category = ['General']
	general, video, audio, text, image, menu = mIwrap.mIWrap(True, selection, None, category, requests)
	printDict(general, 'General')
	printDict(video, 'Video')
	printDict(audio, 'Audio')
	printDict(text, 'Text')
	printDict(image, 'Image')
	printDict(menu, 'Menu')
	return

def test_main():
	general, video, audio, text, image, menu = mIwrap.main()
	printDict(general, 'General')
	printDict(video, 'Video')
